import React, { useState } from "react";
import './Calculadora.css'
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';

export default function Calculadora(){
    const [num, setNum] = useState(0)

    function ChangeNum(e){
        const input = e.target.value
        setNum(input)
    }

    function Operations(state, setState){
        switch(action.type){
            case 'Soma':
                return{...state, num: state.num}
        }
    }

    return (
        <div>
            <Box m={5}/>
            <Container maxWidth="xs">
                <div className="wrapper">
                    <Box m={4}></Box>
                    <h1 className="result">{num}</h1> 
                    <button>AC</button>
                    <button>+/-</button>
                    <button>%</button>
                    <button className="orange">/</button>
                    <button className="gray" onClick={ChangeNum} value={7}>7</button>
                    <button className="gray" onClick={ChangeNum} value={8}>8</button>
                    <button className="gray" onClick={ChangeNum} value={9}>9</button>
                    <button className="orange">X</button>
                    <button className="gray" onClick={ChangeNum} value={4}>4</button>
                    <button className="gray" onClick={ChangeNum} value={5}>5</button>
                    <button className="gray" onClick={ChangeNum} value={6}>6</button>
                    <button className="orange">-</button>
                    <button className="gray" onClick={ChangeNum} value={1}>1</button>
                    <button className="gray" onClick={ChangeNum} value={2}>2</button>
                    <button className="gray" onClick={ChangeNum} value={3}>3</button>
                    <button className="orange">+</button>
                    <button className="gray" onClick={ChangeNum} value={0}>0</button>
                    <button className="gray">,</button>
                    <button>=</button>
                </div>
                <Box m={4}></Box>
        </Container>
        </div>
    )
}
