import React, { useState } from "react";
import './Calculadora.css'
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';

export default function Calculadora(){
    const [valor, setValor] = useState('')
    const [resul, setResult] = useState('')

    const addValue = (texto)=> {
        setResult(num + texto)
    };

    const Calcule = ()=> {
        try {
            const resultCalcule = eval(num);
            setResult(resultCalcule)
        } catch (error) {
            setResult('Erro')
        }
    }

    

    return (
        <div>
            <Box m={5}/>
            <Container maxWidth="xs">
                <div className="wrapper">
                    <Box m={4}></Box>
                    <h1 className="result">{num}</h1> 
                    <button>AC</button>
                    <button>+/-</button>
                    <button>%</button>
                    <button className="orange">/</button>
                    <button className="gray" onClick={()=> addValue('7')}>7</button>
                    <button className="gray" onClick={()=> addValue('8')}>8</button>
                    <button className="gray" onClick={()=> addValue('9')}>9</button>
                    <button className="orange" onClick={()=> addValue('*')}>X</button>
                    <button className="gray" onClick={()=> addValue('4')}>4</button>
                    <button className="gray" onClick={()=> addValue('5')}>5</button>
                    <button className="gray" onClick={()=> addValue('6')}>6</button>
                    <button className="orange" onClick={()=> addValue('-')}>-</button>
                    <button className="gray" onClick={()=> addValue('1')}>1</button>
                    <button className="gray" onClick={()=> addValue('2')}>2</button>
                    <button className="gray" onClick={()=> addValue('3')}>3</button>
                    <button className="orange" onClick={()=> addValue('+')}>+</button>
                    <button className="gray" onClick={()=> addValue('0')}>0</button>
                    <button className="gray" onClick={()=> addValue(',')}>,</button>
                    <button onClick={Calcule}>=</button>
                </div>
                <Box m={4}></Box>
        </Container>
        </div>
    )
}
